import React, { useState } from 'react';
import { Search, User, Key, Map, Activity, Truck, Settings, Book, BarChart2, Code, PlayCircle } from 'lucide-react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Textarea } from '@/components/ui/textarea';

const APISandboxDashboard = () => {
  const [activeTab, setActiveTab] = useState('API Testing');
  const [selectedEndpoint, setSelectedEndpoint] = useState('');
  const [vehicles, setVehicles] = useState([
    { id: 1, x: 100, y: 150, status: 'active' },
    { id: 2, x: 300, y: 200, status: 'maintenance' },
    { id: 3, x: 200, y: 300, status: 'inactive' },
    { id: 4, x: 400, y: 100, status: 'active' },
  ]);

  const navItems = [
    { name: 'API Testing', icon: <Code className="mr-2" /> },
    { name: 'Fleet Management', icon: <Truck className="mr-2" /> },
    { name: 'Maintenance', icon: <Activity className="mr-2" /> },
    { name: 'Rebalancing', icon: <Map className="mr-2" /> },
    { name: 'Analytics', icon: <BarChart2 className="mr-2" /> },
    { name: 'Documentation', icon: <Book className="mr-2" /> },
  ];

  const endpoints = [
    { value: 'get-vehicles', label: 'GET /api/vehicles' },
    { value: 'create-task', label: 'POST /api/tasks' },
    { value: 'update-vehicle', label: 'PUT /api/vehicles/{id}' },
    { value: 'get-stats', label: 'GET /api/stats' },
  ];

  const getVehicleColor = (status) => {
    switch (status) {
      case 'active': return '#22c55e';
      case 'maintenance': return '#eab308';
      case 'inactive': return '#ef4444';
      default: return '#3b82f6';
    }
  };

  const renderMap = () => (
    <svg width="100%" height="100%" viewBox="0 0 1000 800">
      {/* Background */}
      <rect width="1000" height="800" fill="#e6e8e6" />

      {/* Water bodies */}
      <path d="M0 600 Q 250 550, 500 600 T 1000 600 V 800 H 0 Z" fill="#a5d5f5" />

      {/* Main roads */}
      <g stroke="#ffffff" strokeWidth="20">
        <line x1="0" y1="200" x2="1000" y2="200" />
        <line x1="0" y1="400" x2="1000" y2="400" />
        <line x1="0" y1="600" x2="1000" y2="600" />
        <line x1="200" y1="0" x2="200" y2="800" />
        <line x1="500" y1="0" x2="500" y2="800" />
        <line x1="800" y1="0" x2="800" y2="800" />
      </g>

      {/* Secondary roads */}
      <g stroke="#ffffff" strokeWidth="10" strokeDasharray="20,20">
        <line x1="0" y1="100" x2="1000" y2="100" />
        <line x1="0" y1="300" x2="1000" y2="300" />
        <line x1="0" y1="500" x2="1000" y2="500" />
        <line x1="0" y1="700" x2="1000" y2="700" />
        <line x1="100" y1="0" x2="100" y2="800" />
        <line x1="300" y1="0" x2="300" y2="800" />
        <line x1="400" y1="0" x2="400" y2="800" />
        <line x1="600" y1="0" x2="600" y2="800" />
        <line x1="700" y1="0" x2="700" y2="800" />
        <line x1="900" y1="0" x2="900" y2="800" />
      </g>

      {/* Buildings */}
      <g fill="#d1d5db">
        <rect x="20" y="20" width="160" height="160" />
        <rect x="220" y="20" width="100" height="80" />
        <rect x="520" y="20" width="140" height="120" />
        <rect x="820" y="20" width="160" height="160" />
        <rect x="20" y="420" width="120" height="120" />
        <rect x="220" y="420" width="180" height="80" />
        <rect x="520" y="420" width="100" height="100" />
        <rect x="820" y="420" width="140" height="140" />
      </g>

      {/* Parks */}
      <g fill="#86efac">
        <circle cx="400" cy="300" r="80" />
        <circle cx="700" cy="500" r="60" />
        <rect x="50" y="220" width="100" height="160" rx="20" />
      </g>

      {/* Vehicles */}
      {vehicles.map((vehicle) => (
        <g key={vehicle.id}>
          <circle
            cx={vehicle.x * 2}
            cy={vehicle.y * 2}
            r="12"
            fill={getVehicleColor(vehicle.status)}
            stroke="#ffffff"
            strokeWidth="2"
          />
          <text
            x={vehicle.x * 2}
            y={vehicle.y * 2 + 4}
            textAnchor="middle"
            fill="#ffffff"
            fontSize="10"
            fontWeight="bold"
          >
            {vehicle.id}
          </text>
        </g>
      ))}
    </svg>
  );

  return (
    <div className="container mx-auto p-4 bg-gray-100 min-h-screen">
      {/* Top Navigation Bar */}
      <nav className="bg-white shadow-lg rounded-lg mb-6">
        <div className="max-w-7xl mx-auto px-4">
          <div className="flex justify-between h-16">
            <div className="flex space-x-4">
              {navItems.map((item) => (
                <Button
                  key={item.name}
                  variant={activeTab === item.name ? "default" : "ghost"}
                  className="flex items-center"
                  onClick={() => setActiveTab(item.name)}
                >
                  {item.icon}
                  {item.name}
                </Button>
              ))}
            </div>
            <div className="flex items-center space-x-4">
              <Button variant="outline" size="icon">
                <User className="h-4 w-4" />
              </Button>
              <Button variant="outline" size="icon">
                <Key className="h-4 w-4" />
              </Button>
            </div>
          </div>
        </div>
      </nav>

      {/* API Testing Interface */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
        <Card>
          <CardHeader>
            <CardTitle>API Endpoint Testing</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <Select onValueChange={setSelectedEndpoint}>
                <SelectTrigger>
                  <SelectValue placeholder="Select an endpoint" />
                </SelectTrigger>
                <SelectContent>
                  {endpoints.map((endpoint) => (
                    <SelectItem key={endpoint.value} value={endpoint.value}>
                      {endpoint.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <Textarea placeholder="Enter request body (if applicable)" className="h-32" />
              <Button className="w-full">
                <PlayCircle className="mr-2 h-4 w-4" /> Test Endpoint
              </Button>
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardHeader>
            <CardTitle>Response</CardTitle>
          </CardHeader>
          <CardContent>
            <Textarea readOnly className="h-64" placeholder="API response will appear here" />
          </CardContent>
        </Card>
      </div>

      {/* Interactive Fleet Map */}
      <Card className="mb-6">
        <CardHeader>
          <CardTitle>Interactive Fleet Map</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="h-[600px] rounded-lg overflow-hidden relative bg-gray-100">
            {renderMap()}
          </div>
        </CardContent>
      </Card>

      {/* Recommendations */}
      <Card>
        <CardHeader>
          <CardTitle>Recommendations for API Testing</CardTitle>
        </CardHeader>
        <CardContent>
          <ul className="list-disc pl-5 space-y-2">
            <li>Start with simple GET requests to familiarize yourself with the response structure.</li>
            <li>Test edge cases, such as invalid inputs or out-of-range values, to ensure robust error handling.</li>
            <li>Use the API key management feature to test different access levels and permissions.</li>
            <li>Experiment with the fleet management endpoints to understand how vehicle data is structured and updated.</li>
            <li>Try chaining multiple API calls to simulate real-world scenarios, like creating a maintenance task and then updating a vehicle's status.</li>
            <li>Check the Documentation tab for detailed information on request/response formats and any rate limiting policies.</li>
          </ul>
        </CardContent>
      </Card>
    </div>
  );
};

export default APISandboxDashboard;

:root {
    --primary-color: #3a7bd5;
    --secondary-color: #00d2ff;
    --background-color: #f5f7fa;
    --text-color: #333333;
    --card-background: #ffffff;
    --border-radius: 8px;
    --box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
}

body {
    font-family: 'Roboto', Arial, sans-serif;
    line-height: 1.6;
    margin: 0;
    padding: 0;
    background-color: var(--background-color);
    color: var(--text-color);
}

header {
    background: linear-gradient(to right, var(--primary-color), var(--secondary-color));
    color: #ffffff;
    padding: 1rem;
    display: flex;
    justify-content: space-between;
    align-items: center;
    box-shadow: var(--box-shadow);
}

header h1 {
    margin: 0;
    font-size: 1.5rem;
    font-weight: 700;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 2rem 1rem;
}

.panel-container {
    display: flex;
    gap: 2rem;
    margin-bottom: 2rem;
}

.left-panel, .right-panel {
    flex: 1;
    background-color: var(--card-background);
    border-radius: var(--border-radius);
    padding: 1.5rem;
    box-shadow: var(--box-shadow);
}

.central-section {
    background-color: var(--card-background);
    border-radius: var(--border-radius);
    padding: 1.5rem;
    box-shadow: var(--box-shadow);
    margin-bottom: 2rem;
}

select, input, button, textarea {
    width: 100%;
    padding: 0.75rem;
    margin-bottom: 1rem;
    border: 1px solid #e0e0e0;
    border-radius: var(--border-radius);
    font-size: 1rem;
    transition: all 0.3s ease;
}

select:focus, input:focus, textarea:focus {
    outline: none;
    border-color: var(--primary-color);
    box-shadow: 0 0 0 2px rgba(58, 123, 213, 0.2);
}

button {
    background: linear-gradient(to right, var(--primary-color), var(--secondary-color));
    color: #ffffff;
    border: none;
    cursor: pointer;
    font-weight: bold;
    transition: all 0.3s ease;
}

button:hover {
    opacity: 0.9;
    transform: translateY(-2px);
}

button:active {
    transform: translateY(0);
}

.token-container {
    display: flex;
    gap: 0.5rem;
    margin-bottom: 1rem;
}

.token-container input {
    flex-grow: 1;
    margin-bottom: 0;
}

.token-container button {
    width: auto;
    white-space: nowrap;
}

#fleet-map {
    height: 400px;
    border-radius: var(--border-radius);
    overflow: hidden;
    box-shadow: var(--box-shadow);
}

pre {
    background-color: #f4f4f4;
    padding: 1rem;
    border-radius: var(--border-radius);
    overflow-x: auto;
    font-size: 0.9rem;
}

.response-container {
    position: relative;
}

.spinner {
    display: none;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 50px;
    height: 50px;
    border: 5px solid #f3f3f3;
    border-top: 5px solid var(--primary-color);
    border-radius: 50%;
    animation: spin 1s linear infinite;
}

@keyframes spin {
    0% { transform: translate(-50%, -50%) rotate(0deg); }
    100% { transform: translate(-50%, -50%) rotate(360deg); }
}

#notification-area {
    position: fixed;
    top: 1rem;
    right: 1rem;
    z-index: 1000;
}

.notification {
    background-color: #4CAF50;
    color: white;
    padding: 1rem;
    margin-bottom: 0.5rem;
    border-radius: var(--border-radius);
    box-shadow: var(--box-shadow);
    opacity: 0;
    transition: opacity 0.3s ease-in-out;
}

.notification.show {
    opacity: 1;
}

.dashboard-section {
    margin-top: 2rem;
}

.dashboard-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 1rem;
}

.dashboard-card {
    background-color: var(--card-background);
    border-radius: var(--border-radius);
    padding: 1rem;
    box-shadow: var(--box-shadow);
    text-align: center;
}

.dashboard-card h3 {
    margin-top: 0;
    color: var(--primary-color);
}

.dashboard-card p {
    font-size: 1.5rem;
    font-weight: bold;
    margin: 0;
}

.endpoint-list {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
    gap: 1rem;
    margin-bottom: 1rem;
}

.endpoint-item {
    background-color: var(--card-background);
    border-radius: var(--border-radius);
    padding: 1rem;
    box-shadow: var(--box-shadow);
    transition: transform 0.3s ease;
}

.endpoint-item:hover {
    transform: translateY(-5px);
}

.endpoint-item h3 {
    margin-top: 0;
    font-size: 1rem;
    color: var(--primary-color);
}

.endpoint-item p {
    font-size: 0.9rem;
    margin-bottom: 0.5rem;
}

.endpoint-item button {
    width: 100%;
    padding: 0.5rem;
}

@media (max-width: 768px) {
    .panel-container {
        flex-direction: column;
    }

    .left-panel, .right-panel {
        width: 100%;
    }
}

/* Dark mode styles */
body.dark-mode {
    --background-color: #1a1a1a;
    --text-color: #f4f4f4;
    --card-background: #2c2c2c;
}

body.dark-mode header {
    background: linear-gradient(to right, #1a1a1a, #2c2c2c);
}

body.dark-mode pre {
    background-color: #3a3a3a;
    color: #f4f4f4;
}

body.dark-mode #advanced-options,
body.dark-mode #batch-options {
    background-color: #3a3a3a;
}

body.dark-mode .notification {
    background-color: #2c3e50;
}

/* Accessibility improvements */
.visually-hidden {
    position: absolute;
    width: 1px;
    height: 1px;
    margin: -1px;
    padding: 0;
    overflow: hidden;
    clip: rect(0, 0, 0, 0);
    white-space: nowrap;
    border: 0;
}

*:focus {
    outline: 2px solid var(--primary-color);
    outline-offset: 2px;
}

.icon-button {
    background: none;
    border: none;
    font-size: 1.5rem;
    cursor: pointer;
    padding: 0.5rem;
    border-radius: 50%;
    transition: background-color 0.3s ease;
}

.icon-button:hover {
    background-color: rgba(255, 255, 255, 0.1);
}

label {
    display: block;
    margin-bottom: 0.5rem;
    font-weight: bold;
}

.code-block {
    background-color: #f4f4f4;
    border-radius: var(--border-radius);
    padding: 1rem;
    margin-bottom: 1rem;
    font-family: monospace;
    white-space: pre-wrap;
    word-break: break-all;
}

body.dark-mode .code-block {
    background-color: #2c2c2c;
    color: #f4f4f4;
}

document.addEventListener('DOMContentLoaded', () => {
    initializeMap();
    setupDarkModeToggle();
    setupAPIInteraction();
    setupHelpModal();
    updateDashboard();
});

function initializeMap() {
    mapboxgl.accessToken = MAPBOX_ACCESS_TOKEN;
    const map = new mapboxgl.Map({
        container: 'fleet-map',
        style: 'mapbox://styles/mapbox/streets-v11',
        center: [-122.4194, 37.7749],
        zoom: 12
    });

    // Add markers for sample vehicles
    const vehicles = [
        { id: 1, coordinates: [-122.4194, 37.7749], status: 'active' },
        { id: 2, coordinates: [-122.4099, 37.7850], status: 'maintenance' },
        { id: 3, coordinates: [-122.4289, 37.7648], status: 'inactive' },
    ];

    vehicles.forEach(vehicle => {
        const el = document.createElement('div');
        el.className = 'vehicle-marker';
        el.style.backgroundColor = getVehicleColor(vehicle.status);
        el.style.width = '20px';
        el.style.height = '20px';
        el.style.borderRadius = '50%';
        el.style.border = '2px solid white';

        new mapboxgl.Marker(el)
            .setLngLat(vehicle.coordinates)
            .setPopup(new mapboxgl.Popup().setHTML(`Vehicle ${vehicle.id}<br>Status: ${vehicle.status}`))
            .addTo(map);
    });

    // Add zoom and rotation controls
    map.addControl(new mapboxgl.NavigationControl());

    // Add fullscreen control
    map.addControl(new mapboxgl.FullscreenControl());
}

function getVehicleColor(status) {
    switch (status) {
        case 'active': return '#22c55e';
        case 'maintenance': return '#eab308';
        case 'inactive': return '#ef4444';
        default: return '#3b82f6';
    }
}

function setupDarkModeToggle() {
    const darkModeToggle = document.getElementById('dark-mode-toggle');
    const body = document.body;

    darkModeToggle.addEventListener('click', () => {
        body.classList.toggle('dark-mode');
        localStorage.setItem('darkMode', body.classList.contains('dark-mode'));
        darkModeToggle.textContent = body.classList.contains('dark-mode') ? '🌞' : '🌓';
        darkModeToggle.setAttribute('aria-label', body.classList.contains('dark-mode') ? 'Switch to Light Mode' : 'Switch to Dark Mode');
    });

    if (localStorage.getItem('darkMode') === 'true') {
        body.classList.add('dark-mode');
        darkModeToggle.textContent = '🌞';
        darkModeToggle.setAttribute('aria-label', 'Switch to Light Mode');
    }
}

function setupAPIInteraction() {
    const endpointList = document.getElementById('endpoint-list');
    const endpointSearch = document.getElementById('endpoint-search');
    const accessTokenInput = document.getElementById('access-token');
    const generateTestTokenBtn = document.getElementById('generate-test-token-btn');
    const sendRequestBtn = document.getElementById('send-request-btn');
    const requestUrl = document.getElementById('request-url');
    const requestBody = document.getElementById('request-body');
    const responseBody = document.getElementById('response-body');
    const loadingSpinner = document.getElementById('loading-spinner');

    generateTestTokenBtn.addEventListener('click', generateTestToken);
    sendRequestBtn.addEventListener('click', sendRequest);
    endpointSearch.addEventListener('input', filterEndpoints);

    // Populate endpoint list
    const endpoints = getAllEndpoints();
    endpoints.forEach(endpoint => {
        const endpointItem = createEndpointItem(endpoint);
        endpointList.appendChild(endpointItem);
    });

    function createEndpointItem(endpoint) {
        const item = document.createElement('div');
        item.className = 'endpoint-item';
        item.innerHTML = `
            <h3>${endpoint.method} ${endpoint.path}</h3>
            <p>${endpoint.description}</p>
            <button class="select-endpoint-btn">Select</button>
        `;
        item.querySelector('.select-endpoint-btn').addEventListener('click', () => selectEndpoint(endpoint));
        return item;
    }

    function selectEndpoint(endpoint) {
        requestUrl.textContent = `${endpoint.method} ${endpoint.path}`;
        requestBody.textContent = JSON.stringify(endpoint.sampleBody || {}, null, 2);
        highlightCode();
    }

    function filterEndpoints() {
        const searchTerm = endpointSearch.value.toLowerCase();
        const endpointItems = endpointList.querySelectorAll('.endpoint-item');

        endpointItems.forEach(item => {
            const endpointText = item.textContent.toLowerCase();
            if (endpointText.includes(searchTerm)) {
                item.style.display = 'block';
            } else {
                item.style.display = 'none';
            }
        });
    }

    function highlightCode() {
        hljs.highlightElement(requestBody);
        hljs.highlightElement(responseBody);
    }

    function generateTestToken() {
        fetch('/api/v1/auth/generate-test-token', {
            method: 'POST',
        })
        .then(response => response.json())
        .then(data => {
            accessTokenInput.value = data.test_token;
            showNotification('Test API Access Token generated successfully.', 'success');
        })
        .catch(error => {
            showNotification('Failed to generate Test API Access Token.', 'error');
        });
    }

    function sendRequest() {
        const accessToken = accessTokenInput.value;
        const url = requestUrl.textContent.split(' ')[1];
        const method = requestUrl.textContent.split(' ')[0];

        if (!url || !accessToken) {
            showNotification('Please select an endpoint and enter an access token.', 'error');
            return;
        }

        showNotification('Sending request...', 'info');
        loadingSpinner.style.display = 'block';
        responseBody.style.opacity = '0.5';

        let body;
        try {
            body = JSON.parse(requestBody.textContent);
        } catch (error) {
            showNotification('Invalid JSON in request body.', 'error');
            loadingSpinner.style.display = 'none';
            responseBody.style.opacity = '1';
            return;
        }

        fetch(url, {
            method: method,
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${accessToken}`
            },
            body: method !== 'GET' ? JSON.stringify(body) : undefined
        })
        .then(response => response.json())
        .then(data => {
            responseBody.textContent = JSON.stringify(data, null, 2);
            highlightCode();
            showNotification('Request completed successfully.', 'success');
        })
        .catch(error => {
            responseBody.textContent = `Error: ${error.message}`;
            showNotification('An error occurred while sending the request.', 'error');
        })
        .finally(() => {
            loadingSpinner.style.display = 'none';
            responseBody.style.opacity = '1';
        });
    }
}

function getAllEndpoints() {
    return [
        {
            method: 'GET',
            path: '/api/v1/fleet/vehicles',
            description: 'Get all vehicles',
            sampleBody: {}
        },
        {
            method: 'POST',
            path: '/api/v1/fleet/vehicles',
            description: 'Create a new vehicle',
            sampleBody: {
                "name": "Vehicle 1",
                "type": "car",
                "status": "active"
            }
        },
        {
            method: 'GET',
            path: '/api/v1/maintenance/tasks',
            description: 'Get all maintenance tasks',
            sampleBody: {}
        },
        {
            method: 'POST',
            path: '/api/v1/maintenance/tasks',
            description: 'Create a new maintenance task',
            sampleBody: {
                "vehicle_id": 1,
                "description": "Oil change",
                "due_date": "2024-10-01"
            }
        },
        {
            method: 'GET',
            path: '/api/v1/rebalancing/stations',
            description: 'Get all stations',
            sampleBody: {}
        },
        {
            method: 'POST',
            path: '/api/v1/rebalancing/task',
            description: 'Schedule a rebalancing task',
            sampleBody: {
                "from_station_id": 1,
                "to_station_id": 2,
                "num_bikes": 5
            }
        },
        {
            method: 'GET',
            path: '/api/v1/user/activity',
            description: 'Get user activity logs',
            sampleBody: {}
        },
        {
            method: 'POST',
            path: '/api/v1/user/access',
            description: 'Manage user access',
            sampleBody: {
                "username": "john_doe",
                "role": "admin",
                "action": "grant"
            }
        }
    ];
}

function setupHelpModal() {
    const modal = document.getElementById('help-modal');
    const btn = document.getElementById('help-button');
    const closeBtn = modal.querySelector('.close');

    btn.onclick = function() {
        modal.style.display = 'block';
        modal.setAttribute('aria-hidden', 'false');
    }

    closeBtn.onclick = function() {
        modal.style.display = 'none';
        modal.setAttribute('aria-hidden', 'true');
    }

    window.onclick = function(event) {
        if (event.target == modal) {
            modal.style.display = 'none';
            modal.setAttribute('aria-hidden', 'true');
        }
    }
}

function updateDashboard() {
    // This function should fetch real data from your API
    // For now, we'll use placeholder data
    document.getElementById('total-vehicles').textContent = '150';
