                backgroundColor: [
                    'rgba(75, 192, 192, 0.6)',
                    'rgba(255, 99, 132, 0.6)',
                    'rgba(255, 206, 86, 0.6)',
                    'rgba(54, 162, 235, 0.6)',
                ],
            }]
        },
        options: {
            responsive: true,
            title: {
                display: true,
                text: 'Fleet Status Distribution'
            }
        }
    });
}

