        })
        .then(data => {
            document.getElementById('response').textContent = JSON.stringify(data, null, 2);
            document.getElementById('error').textContent = '';

            if (endpoint === '/fleet/status') {
                updateFleetStatusChart(data);
            } else if (endpoint === '/reports/maintenance') {
                updateMaintenanceHistoryChart(data);
            }
        })
        .catch(error => {
            console.error('Error:', error);
            document.getElementById('error').textContent = `Error: ${error.message}`;
            document.getElementById('response').textContent = '';
        });
}

function updateFleetStatusChart(data) {
    const ctx = document.getElementById('fleetStatusChart').getContext('2d');
    const statusBreakdown = data.status_breakdown;
    const labels = Object.keys(statusBreakdown);
    const values = Object.values(statusBreakdown);

    if (fleetStatusChart) {
        fleetStatusChart.destroy();
    }

    fleetStatusChart = new Chart(ctx, {
        type: 'pie',
        data: {
            labels: labels,
            datasets: [{
                data: values,
                backgroundColor: [
                    'rgba(75, 192, 192, 0.6)',
                    'rgba(255, 99, 132, 0.6)',
                    'rgba(255, 206, 86, 0.6)',
                    'rgba(54, 162, 235, 0.6)',
                ],
            }]
        },
        options: {
            responsive: true,
            title: {
                display: true,
                text: 'Fleet Status Distribution'
            },
            plugins: {
                tooltip: {
                    callbacks: {
                        label: (context) => {
                            const label = context.label || '';
                            const value = context.parsed || 0;
                            const total = context.dataset.data.reduce((acc, curr) => acc + curr, 0);
                            const percentage = ((value / total) * 100).toFixed(2);
                            return `${label}: ${value} (${percentage}%)`;
                        }
                    }
                }
            }
        }
    });
}

function updateMaintenanceHistoryChart(data) {
    const ctx = document.getElementById('maintenanceHistoryChart').getContext('2d');
    const dates = Array.from({length: 30}, (_, i) => {
        const d = new Date();
        d.setDate(d.getDate() - i);
        return d.toISOString().split('T')[0];
    }).reverse();

    const maintenanceCounts = dates.map(date => {
        return data.maintenance_history[date] || 0;
    });

    if (maintenanceHistoryChart) {
        maintenanceHistoryChart.destroy();
    }

    maintenanceHistoryChart = new Chart(ctx, {
        type: 'line',
        data: {
            labels: dates,
            datasets: [{
                label: 'Maintenance Tasks',
                data: maintenanceCounts,
                borderColor: 'rgba(75, 192, 192, 1)',
                backgroundColor: 'rgba(75, 192, 192, 0.2)',
                tension: 0.1
            }]
        },
        options: {
            responsive: true,
            title: {
                display: true,
                text: 'Maintenance History (Last 30 Days)'
            },
            scales: {
                x: {
                    type: 'time',
                    time: {
                        unit: 'day',
                        displayFormats: {
                            day: 'MMM D'
                        }
                    },
                    title: {
                        display: true,
                        text: 'Date'
                    }
                },
                y: {
                    beginAtZero: true,
                    title: {
                        display: true,
                        text: 'Number of Tasks'
                    }
                }
            }
        }
    });
}

