function setupNavigation() {
    const navButtons = document.querySelectorAll('.nav-button');
    const tabContents = document.querySelectorAll('.tab-content');

    navButtons.forEach(button => {
        button.addEventListener('click', () => {
            const tabName = button.getAttribute('data-tab');

            // Remove active class from all buttons and tab contents
            navButtons.forEach(btn => btn.classList.remove('active'));
            tabContents.forEach(content => {
                content.classList.remove('active');
                content.style.display = 'none';
            });

            // Add active class to clicked button and corresponding tab content
            button.classList.add('active');
            const activeTab = document.getElementById(tabName);
            activeTab.style.display = 'block';
            setTimeout(() => {
                activeTab.classList.add('active');
            }, 50);
        });
    });
}

function setupAPITesting() {
    const endpointSelect = document.getElementById('endpoint-select');
    const requestBody = document.getElementById('request-body');
    const testButton = document.getElementById('test-endpoint');
    const responseArea = document.getElementById('response-area');
    const saveRequestButton = document.getElementById('save-request');
    const loadRequestButton = document.getElementById('load-request');

    testButton.addEventListener('click', () => {
        const endpoint = endpointSelect.value;
        const body = requestBody.textContent;

        if (!endpoint) {
            responseArea.textContent = 'Please select an endpoint';
            hljs.highlightElement(responseArea);
            return;
        }

        // Map endpoint to actual API route
        const apiRoutes = {
            'get-vehicles': '/fleet/vehicles',
            'create-task': '/fleet/task',
            'update-vehicle': '/fleet/vehicles/1', // Assuming ID 1 for example
            'get-stats': '/reports/usage'
        };

        const method = endpoint.startsWith('get') ? 'GET' : 'POST';
        sendRequest(apiRoutes[endpoint], method, body ? JSON.parse(body) : null);
    });

    saveRequestButton.addEventListener('click', () => {
        const requestData = {
            endpoint: endpointSelect.value,
            body: requestBody.textContent
        };
        localStorage.setItem('savedRequest', JSON.stringify(requestData));
        alert('Request saved successfully');
    });

    loadRequestButton.addEventListener('click', () => {
        const savedRequest = JSON.parse(localStorage.getItem('savedRequest'));
        if (savedRequest) {
            endpointSelect.value = savedRequest.endpoint;
            requestBody.textContent = savedRequest.body;
            hljs.highlightElement(requestBody);
        } else {
            alert('No saved request found');
        }
    });
}

function sendRequest(endpoint, method, body = null) {
    const options = {
        method: method,
        headers: {
            'Content-Type': 'application/json'
        }
    };

    if (body) {
        options.body = JSON.stringify(body);
    }

    // Show loading animation
    document.getElementById('response-area').textContent = 'Loading...';
    hljs.highlightElement(document.getElementById('response-area'));

    fetch(endpoint, options)
        .then(response => {
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            return response.json();
        })
        .then(data => {
            document.getElementById('response-area').textContent = JSON.stringify(data, null, 2);
            hljs.highlightElement(document.getElementById('response-area'));
        })
        .catch(error => {
            console.error('Error:', error);
            document.getElementById('response-area').textContent = `Error: ${error.message}`;
