  margin: 0;
  padding: 0;
  background-color: var(--background-color);
  color: var(--text-color);
}

.container {
  display: flex;
}

.sidebar {
  width: var(--sidebar-width);
  height: 100vh;
  background-color: var(--primary-color);
  color: #fff;
  padding: 20px;
  position: fixed;
}

.sidebar h2 {
  margin-bottom: 20px;
}

.sidebar ul {
  list-style-type: none;
  padding: 0;
}

.sidebar ul li {
  margin-bottom: 10px;
}

.sidebar ul li a {
  color: #fff;
  text-decoration: none;
  display: block;
  padding: 10px;
  border-radius: 5px;
  transition: background-color 0.3s;
}

.sidebar ul li a:hover {
  background-color: rgba(255, 255, 255, 0.1);
}

.main-content {
  margin-left: var(--sidebar-width);
  padding: 20px;
  width: calc(100% - var(--sidebar-width));
}

.card {
  background-color: var(--card-background);
  border-radius: 8px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  margin-bottom: 20px;
  overflow: hidden;
}

.card-header {
  background-color: var(--primary-color);
  color: #fff;
  padding: 15px 20px;
}

.card-header h2 {
  margin: 0;
}

.card-content {
  padding: 20px;
}

#fleet-map {
  height: 500px;
  background-color: #e5e7eb;
  border-radius: 8px;
}

.api-testing-container {
  display: flex;
  flex-direction: column;
  gap: 20px;
}

.endpoint-selection {
  display: flex;
  gap: 10px;
}

#endpoint-search, #endpoint-select {
  flex: 1;
  padding: 10px;
  border: 1px solid #d1d5db;
  border-radius: 5px;
}

.request-response-container {
  display: flex;
  gap: 20px;
}

.request-container, .response-container {
  flex: 1;
}

pre {
  background-color: #f8f8f8;
  padding: 15px;
  border-radius: 5px;
  overflow-x: auto;
}

button {
  background-color: var(--secondary-color);
  color: #fff;
  border: none;
  padding: 10px 20px;
  border-radius: 5px;
  cursor: pointer;
  transition: background-color 0.3s;
}

button:hover {
  background-color: #27ae60;
}

.loading-spinner {
  display: none;
  width: 50px;
  height: 50px;
  border: 5px solid #f3f3f3;
  border-top: 5px solid var(--primary-color);
  border-radius: 50%;
  animation: spin 1s linear infinite;
  margin: 20px auto;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

/* Dark mode styles */
body.dark-mode {
  --background-color: #1a1a1a;
  --text-color: #f4f4f4;
  --card-background: #2c2c2c;
}

body.dark-mode .sidebar {
  background-color: #2c3e50;
}

body.dark-mode .card-header {
  background-color: #34495e;
}

body.dark-mode pre {
  background-color: #3a3a3a;
}

@media (max-width: 768px) {
  .container {
    flex-direction: column;
  }

  .sidebar {
    width: 100%;
    height: auto;
    position: static;
  }

  .main-content {
    margin-left: 0;
    width: 100%;
  }

  .request-response-container {
    flex-direction: column;
  }
}

