function updateCharts() {
    // Simulate updating charts with new data
    charts.vehicleStatus.data.datasets[0].data = [
        Math.floor(Math.random() * 50) + 50,
        Math.floor(Math.random() * 20) + 10,
        Math.floor(Math.random() * 20) + 5
    ];
    charts.vehicleStatus.update();

    charts.maintenanceTasks.data.datasets[0].data = charts.maintenanceTasks.data.datasets[0].data.map(() => Math.floor(Math.random() * 20));
    charts.maintenanceTasks.update();

