  width: 8px;
}

::-webkit-scrollbar-track {
  background: #f1f1f1;
}

::-webkit-scrollbar-thumb {
  background: var(--primary-color);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: #3a7ac0;
}

tool_observations.updated_content:
:root {
  --primary-color: #4a90e2;
  --secondary-color: #50c878;
  --background-color: #f8f9fa;
  --text-color: #333;
  --card-background: #fff;
  --sidebar-width: 250px;
  --border-radius: 8px;
  --box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
}

body {
  font-family: 'Roboto', Arial, sans-serif;
  line-height: 1.6;
  margin: 0;
  padding: 0;
  background-color: var(--background-color);
  color: var(--text-color);
  transition: background-color 0.3s ease, color 0.3s ease;
}

.container {
  display: flex;
}

.sidebar {
  width: var(--sidebar-width);
  height: 100vh;
  background-color: var(--primary-color);
  color: #fff;
  padding: 20px;
  position: fixed;
  transition: all 0.3s ease;
  z-index: 1000;
}

.sidebar h2 {
  margin-bottom: 30px;
  font-size: 24px;
  text-align: center;
}

.sidebar ul {
  list-style-type: none;
  padding: 0;
}

.sidebar ul li {
  margin-bottom: 15px;
}

.sidebar ul li a {
  color: #fff;
  text-decoration: none;
  display: block;
  padding: 10px 15px;
  border-radius: var(--border-radius);
  transition: background-color 0.3s, transform 0.2s;
}

.sidebar ul li a:hover {
  background-color: rgba(255, 255, 255, 0.1);
  transform: translateX(5px);
}

.main-content {
  margin-left: var(--sidebar-width);
  padding: 30px;
  width: calc(100% - var(--sidebar-width));
  transition: margin-left 0.3s ease;
}

.card {
  background-color: var(--card-background);
  border-radius: var(--border-radius);
  box-shadow: var(--box-shadow);
  margin-bottom: 30px;
  overflow: hidden;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.card:hover {
  transform: translateY(-5px);
  box-shadow: 0 6px 12px rgba(0, 0, 0, 0.15);
}

.card-header {
  background-color: var(--primary-color);
  color: #fff;
  padding: 15px 20px;
}

.card-header h2 {
  margin: 0;
  font-size: 20px;
}

.card-content {
  padding: 20px;
}

#fleet-map {
  height: 500px;
  background-color: #e5e7eb;
  border-radius: var(--border-radius);
}

.api-testing-container {
  display: flex;
  flex-direction: column;
  gap: 20px;
}

.endpoint-selection {
  display: flex;
  gap: 10px;
}

#endpoint-search, #endpoint-select {
  flex: 1;
  padding: 10px;
  border: 1px solid #d1d5db;
  border-radius: var(--border-radius);
  font-size: 14px;
  transition: border-color 0.3s ease;
}

#endpoint-search:focus, #endpoint-select:focus {
  outline: none;
  border-color: var(--primary-color);
}

.request-response-container {
  display: flex;
  gap: 20px;
}

.request-container, .response-container {
  flex: 1;
}

pre {
  background-color: #f8f8f8;
  padding: 15px;
  border-radius: var(--border-radius);
  overflow-x: auto;
  font-size: 14px;
}

button {
  background-color: var(--secondary-color);
  color: #fff;
  border: none;
  padding: 10px 20px;
  border-radius: var(--border-radius);
  cursor: pointer;
  transition: background-color 0.3s, transform 0.2s;
  font-size: 14px;
  font-weight: bold;
}

button:hover {
  background-color: #45b36b;
  transform: translateY(-2px);
}

.loading-spinner {
  display: none;
  width: 50px;
  height: 50px;
  border: 5px solid #f3f3f3;
  border-top: 5px solid var(--primary-color);
  border-radius: 50%;
  animation: spin 1s linear infinite;
  margin: 20px auto;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

/* Dark mode styles */
body.dark-mode {
  --background-color: #1a1a1a;
  --text-color: #f4f4f4;
  --card-background: #2c2c2c;
}

body.dark-mode .sidebar {
  background-color: #2c3e50;
}

body.dark-mode .card-header {
  background-color: #34495e;
}

body.dark-mode pre {
  background-color: #3a3a3a;
  color: #f4f4f4;
}

/* Responsive styles */
@media (max-width: 768px) {
  .container {
    flex-direction: column;
  }

  .sidebar {
    width: 100%;
    height: auto;
    position: static;
  }

  .main-content {
    margin-left: 0;
    width: 100%;
  }

  .request-response-container {
    flex-direction: column;
  }
}

/* New styles for improved UI */
.button-group {
  display: flex;
  gap: 10px;
  margin-top: 15px;
}

.notification {
  position: fixed;
  top: 20px;
  right: 20px;
  background-color: var(--primary-color);
  color: #fff;
  padding: 10px 20px;
  border-radius: var(--border-radius);
  box-shadow: var(--box-shadow);
  opacity: 0;
  transition: opacity 0.3s ease, transform 0.3s ease;
  z-index: 1001;
}

.notification.show {
  opacity: 1;
  transform: translateY(10px);
}

#dark-mode-toggle {
  background-color: transparent;
  border: 2px solid #fff;
  color: #fff;
  padding: 8px 15px;
  margin-top: 20px;
  width: 100%;
  transition: background-color 0.3s, color 0.3s;
}

#dark-mode-toggle:hover {
  background-color: rgba(255, 255, 255, 0.1);
}

.chart-container {
  position: relative;
  height: 300px;
}

/* New styles for better responsiveness and aesthetics */
.full-width {
  width: 100%;
}

input[type="text"], select, textarea {
  width: 100%;
  padding: 10px;
  margin-bottom: 10px;
  border: 1px solid #d1d5db;
  border-radius: var(--border-radius);
  font-size: 14px;
  transition: border-color 0.3s ease;
}

input[type="text"]:focus, select:focus, textarea:focus {
  outline: none;
  border-color: var(--primary-color);
}

.card-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 20px;
}

.vehicle-details {
  background-color: var(--card-background);
  border-radius: var(--border-radius);
  padding: 15px;
  margin-top: 15px;
  box-shadow: var(--box-shadow);
}

.vehicle-details h3 {
  margin-top: 0;
  color: var(--primary-color);
}

.api-description {
  margin-bottom: 15px;
  font-style: italic;
  color: #666;
}

/* Improve form elements */
textarea {
  min-height: 100px;
  resize: vertical;
}

/* Add some animations */
@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

.fade-in {
  animation: fadeIn 0.5s ease-in;
}

/* Improve scrollbar appearance */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: #f1f1f1;
}

::-webkit-scrollbar-thumb {
  background: var(--primary-color);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: #3a7ac0;
}

/* Add a subtle hover effect to the fleet map */
#fleet-map:hover {
  box-shadow: 0 8px 16px rgba(0, 0, 0, 0.1);
}

/* Improve the appearance of the maintenance form */
#schedule-maintenance-form {
  display: flex;
  flex-direction: column;
  gap: 10px;
}

#schedule-maintenance-form button {
  align-self: flex-start;
}

/* Add a subtle transition to chart containers */
.chart-container {
  transition: opacity 0.3s ease;
}

.chart-container:hover {
  opacity: 0.9;
}

