    { id: 1, x: -74.006, y: 40.7128, status: 'active' },
    { id: 2, x: -73.986, y: 40.7308, status: 'maintenance' },
    { id: 3, x: -73.966, y: 40.7488, status: 'inactive' },
    { id: 4, x: -74.026, y: 40.7218, status: 'active' },
];

let charts = {};

document.addEventListener('DOMContentLoaded', () => {
    try {
        initializeMap();
        setupRealTimeUpdates();
        initializeCharts();
        setupApiTestingInterface();
        setupSidebarNavigation();

        // Update charts periodically
        setInterval(updateCharts, 5000);

        // Set up dark mode toggle
        const darkModeToggle = document.getElementById('dark-mode-toggle');
        if (darkModeToggle) {
            darkModeToggle.addEventListener('click', toggleDarkMode);

            // Check for saved dark mode preference
            if (localStorage.getItem('darkMode') === 'true') {
                document.body.classList.add('dark-mode');
            }
        }

        // Initialize syntax highlighting
        hljs.highlightAll();
    } catch (error) {
        console.error('Error initializing application:', error);
        showNotification('Error initializing application. Please try refreshing the page.');
    }
});

function setupSidebarNavigation() {
    const sidebarLinks = document.querySelectorAll('.sidebar a');
    sidebarLinks.forEach(link => {
        link.addEventListener('click', (e) => {
            e.preventDefault();
            const targetId = link.getAttribute('href').substring(1);
            const targetElement = document.getElementById(targetId);
            if (targetElement) {
                targetElement.scrollIntoView({ behavior: 'smooth' });
            }
        });
    });
}

function initializeMap() {
    mapboxgl.accessToken = 'MAPBOX_ACCESS_TOKEN'; // Replace with actual token
    map = new mapboxgl.Map({
        container: 'fleet-map',
        style: 'mapbox://styles/mapbox/streets-v11',
        center: [-74.006, 40.7128], // Default to New York City
        zoom: 12
    });

    map.on('load', function () {
        // Add markers for vehicles
        vehicles.forEach(vehicle => {
            const el = document.createElement('div');
            el.className = 'vehicle-marker';
            el.style.backgroundColor = getVehicleColor(vehicle.status);
            el.style.width = '20px';
            el.style.height = '20px';
            el.style.borderRadius = '50%';
            el.style.cursor = 'pointer';

            new mapboxgl.Marker(el)
                .setLngLat([vehicle.x, vehicle.y])
                .addTo(map);

            el.addEventListener('click', () => showVehicleDetails(vehicle));
        });
    });
}

function getVehicleColor(status) {
    switch (status) {
        case 'active': return '#22c55e';
        case 'maintenance': return '#eab308';
        case 'inactive': return '#ef4444';
        default: return '#3b82f6';
    }
}

function showVehicleDetails(vehicle) {
    const detailsContainer = document.getElementById('vehicle-details');
    detailsContainer.innerHTML = `
        <h3>Vehicle ${vehicle.id}</h3>
        <p>Status: ${vehicle.status}</p>
        <p>Location: (${vehicle.x.toFixed(4)}, ${vehicle.y.toFixed(4)})</p>
    `;
    detailsContainer.style.display = 'block';
}

function setupRealTimeUpdates() {
    // Simulating real-time updates
    setInterval(() => {
        const randomVehicle = vehicles[Math.floor(Math.random() * vehicles.length)];
        randomVehicle.status = ['active', 'maintenance', 'inactive'][Math.floor(Math.random() * 3)];
        updateVehicleOnMap(randomVehicle);
        showNotification(`Vehicle ${randomVehicle.id} status updated to ${randomVehicle.status}`);
    }, 5000);
}

function updateVehicleOnMap(vehicle) {
    const marker = map.getCanvas().querySelector(`.vehicle-marker[data-id="${vehicle.id}"]`);
    if (marker) {
        marker.style.backgroundColor = getVehicleColor(vehicle.status);
    }
}

