document.addEventListener('DOMContentLoaded', () => {
    initializeMap();
    setupDarkModeToggle();
    setupAPIInteraction();
});

function initializeMap() {
    mapboxgl.accessToken = MAPBOX_ACCESS_TOKEN;
    const map = new mapboxgl.Map({
        container: 'fleet-map',
        style: 'mapbox://styles/mapbox/streets-v11',
        center: [-122.4194, 37.7749],
        zoom: 12
    });

    // Add markers for sample vehicles
    const vehicles = [
        { id: 1, coordinates: [-122.4194, 37.7749], status: 'active' },
        { id: 2, coordinates: [-122.4099, 37.7850], status: 'maintenance' },
        { id: 3, coordinates: [-122.4289, 37.7648], status: 'inactive' },
    ];

    vehicles.forEach(vehicle => {
        const el = document.createElement('div');
        el.className = 'vehicle-marker';
        el.style.backgroundColor = getVehicleColor(vehicle.status);
        el.style.width = '20px';
        el.style.height = '20px';
        el.style.borderRadius = '50%';
        el.style.border = '2px solid white';

        new mapboxgl.Marker(el)
            .setLngLat(vehicle.coordinates)
            .setPopup(new mapboxgl.Popup().setHTML(`Vehicle ${vehicle.id}<br>Status: ${vehicle.status}`))
            .addTo(map);
    });
}

function getVehicleColor(status) {
    switch (status) {
        case 'active': return '#22c55e';
        case 'maintenance': return '#eab308';
        case 'inactive': return '#ef4444';
        default: return '#3b82f6';
    }
}

function setupDarkModeToggle() {
    const darkModeToggle = document.getElementById('dark-mode-toggle');
    const body = document.body;

    darkModeToggle.addEventListener('click', () => {
        body.classList.toggle('dark-mode');
        localStorage.setItem('darkMode', body.classList.contains('dark-mode'));
    });

    if (localStorage.getItem('darkMode') === 'true') {
        body.classList.add('dark-mode');
    }
}

function setupAPIInteraction() {
    const apiCategorySelect = document.getElementById('api-category-select');
    const apiEndpointSelect = document.getElementById('api-endpoint-select');
    const optionalParameters = document.getElementById('optional-parameters');
    const accessTokenInput = document.getElementById('access-token');
    const tryExampleBtn = document.getElementById('try-example-btn');
    const sendRequestBtn = document.getElementById('send-request-btn');
    const requestUrl = document.getElementById('request-url');
    const responseBody = document.getElementById('response-body');

    apiCategorySelect.addEventListener('change', updateEndpoints);
    apiEndpointSelect.addEventListener('change', updateOptionalParameters);
    tryExampleBtn.addEventListener('click', loadExample);
    sendRequestBtn.addEventListener('click', sendRequest);

    function updateEndpoints() {
        const category = apiCategorySelect.value;
        apiEndpointSelect.innerHTML = '<option value="">Select an API endpoint</option>';

        const endpoints = getEndpointsForCategory(category);
        endpoints.forEach(endpoint => {
            const option = document.createElement('option');
            option.value = endpoint.value;
            option.textContent = endpoint.label;
            apiEndpointSelect.appendChild(option);
        });
    }

    function getEndpointsForCategory(category) {
        switch (category) {
            case 'fleet':
                return [
                    { value: 'get-vehicles', label: 'GET /api/v1/vehicles' },
                    { value: 'update-vehicle', label: 'PATCH /api/v1/vehicles/{vehicleId}' },
                    { value: 'get-fleet-status', label: 'GET /api/v1/fleet/status' }
                ];
            case 'user':
                return [
                    { value: 'get-user', label: 'GET /api/v1/auth/me' },
                    { value: 'register-user', label: 'POST /api/v1/auth/register' },
                    { value: 'get-user-roles', label: 'GET /api/v1/auth/roles' }
                ];
            case 'trip':
                return [
                    { value: 'start-trip', label: 'POST /api/v1/trips' },
                    { value: 'end-trip', label: 'PATCH /api/v1/trips/{tripId}' },
                    { value: 'get-trip-details', label: 'GET /api/v1/trips/{tripId}' }
                ];
            case 'maintenance':
                return [
                    { value: 'schedule-maintenance', label: 'POST /api/v1/maintenance' },
                    { value: 'get-maintenance-history', label: 'GET /api/v1/vehicles/{vehicleId}/maintenance' },
                    { value: 'create-alert', label: 'POST /api/v1/alerts' }
                ];
            case 'analytics':
                return [
                    { value: 'get-usage-stats', label: 'GET /api/v1/analytics/usage' },
                    { value: 'generate-report', label: 'POST /api/v1/reports' },
                    { value: 'get-report', label: 'GET /api/v1/reports/{reportId}' }
                ];
            case 'payment':
                return [
                    { value: 'create-invoice', label: 'POST /api/v1/invoices' },
                    { value: 'get-invoice', label: 'GET /api/v1/invoices/{invoiceId}' },
                    { value: 'process-payment', label: 'POST /api/v1/payments' }
                ];
            case 'location':
                return [
                    { value: 'create-geofence', label: 'POST /api/v1/geofences' },
                    { value: 'update-geofence', label: 'PATCH /api/v1/geofences/{geofenceId}' },
                    { value: 'get-geofences', label: 'GET /api/v1/geofences' }
                ];
            default:
                return [];
        }
    }

    function updateOptionalParameters() {
        const endpoint = apiEndpointSelect.value;
        optionalParameters.innerHTML = '';

        switch (endpoint) {
            case 'update-vehicle':
                optionalParameters.innerHTML = `
                    <input type="text" id="vehicle-id" placeholder="Vehicle ID">
                    <input type="text" id="vehicle-status" placeholder="New Status">
                `;
                break;
            case 'register-user':
                optionalParameters.innerHTML = `
                    <input type="text" id="username" placeholder="Username">
                    <input type="email" id="email" placeholder="Email">
                    <input type="password" id="password" placeholder="Password">
                `;
                break;
            case 'start-trip':
                optionalParameters.innerHTML = `
                    <input type="text" id="vehicle-id" placeholder="Vehicle ID">
                    <input type="text" id="user-id" placeholder="User ID">
                    <input type="text" id="start-location" placeholder="Start Location">
                `;
                break;
            case 'schedule-maintenance':
                optionalParameters.innerHTML = `
                    <input type="text" id="vehicle-id" placeholder="Vehicle ID">
                    <input type="text" id="maintenance-type" placeholder="Maintenance Type">
                    <input type="date" id="scheduled-date" placeholder="Scheduled Date">
                `;
                break;
            case 'generate-report':
                optionalParameters.innerHTML = `
                    <input type="text" id="report-type" placeholder="Report Type">
                    <input type="date" id="start-date" placeholder="Start Date">
                    <input type="date" id="end-date" placeholder="End Date">
                `;
                break;
            case 'create-geofence':
                optionalParameters.innerHTML = `
                    <input type="text" id="geofence-name" placeholder="Geofence Name">
                    <textarea id="coordinates" placeholder="Coordinates (lat,lon pairs)"></textarea>
                `;
                break;
        }
    }

    function loadExample() {
        const endpoint = apiEndpointSelect.value;
        switch (endpoint) {
            case 'get-vehicles':
                accessTokenInput.value = 'example_token';
                break;
            case 'update-vehicle':
                accessTokenInput.value = 'example_token';
                document.getElementById('vehicle-id').value = '1';
                document.getElementById('vehicle-status').value = 'maintenance';
                break;
            case 'register-user':
                accessTokenInput.value = 'example_token';
                document.getElementById('username').value = 'john_doe';
                document.getElementById('email').value = 'john@example.com';
                document.getElementById('password').value = 'password123';
                break;
            case 'create-geofence':
                accessTokenInput.value = 'example_token';
                document.getElementById('geofence-name').value = 'Downtown Area';
                document.getElementById('coordinates').value = '37.7749,-122.4194\n37.7750,-122.4094\n37.7848,-122.4094\n37.7848,-122.4194';
                break;
        }
        showNotification('Example data loaded successfully.');
    }

    function sendRequest() {
        const endpoint = apiEndpointSelect.value;
        const accessToken = accessTokenInput.value;

        if (!endpoint || !accessToken) {
            showNotification('Please select an endpoint and enter an access token.', 'error');
            return;
        }

        const url = getApiUrl(endpoint);
        const method = getApiMethod(endpoint);
        const body = getRequestBody(endpoint);

        requestUrl.textContent = `${method}: ${url}`;
        requestUrl.className = 'code-block';

        showNotification('Sending request...', 'info');

        fetch(url, {
            method: method,
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${accessToken}`
            },
            body: method !== 'GET' ? JSON.stringify(body) : undefined
        })
        .then(response => response.json())
        .then(data => {
            responseBody.textContent = JSON.stringify(data, null, 2);
            hljs.highlightElement(responseBody);
            showNotification('Request completed successfully.', 'success');
        })
        .catch(error => {
            responseBody.textContent = `Error: ${error.message}`;
            showNotification('An error occurred while sending the request.', 'error');
        });
    }

    function getApiUrl(endpoint) {
        const baseUrl = 'https://api.lattis.com/api/v1';
        switch (endpoint) {
            case 'get-vehicles':
                return `${baseUrl}/vehicles`;
            case 'update-vehicle':
                const vehicleId = document.getElementById('vehicle-id').value;
                return `${baseUrl}/vehicles/${vehicleId}`;
            case 'get-user':
                return `${baseUrl}/auth/me`;
            case 'register-user':
                return `${baseUrl}/auth/register`;
            default:
                return baseUrl;
        }
    }

    function getApiMethod(endpoint) {
        switch (endpoint) {
            case 'get-vehicles':
            case 'get-user':
            case 'get-fleet-status':
            case 'get-user-roles':
            case 'get-trip-details':
            case 'get-maintenance-history':
            case 'get-usage-stats':
            case 'get-report':
            case 'get-invoice':
            case 'get-geofences':
                return 'GET';
            case 'update-vehicle':
            case 'end-trip':
            case 'update-geofence':
                return 'PATCH';
            case 'register-user':
            case 'start-trip':
            case 'schedule-maintenance':
            case 'create-alert':
            case 'generate-report':
            case 'create-invoice':
            case 'process-payment':
            case 'create-geofence':
                return 'POST';
            default:
                return 'GET';
        }
    }

    function getRequestBody(endpoint) {
        switch (endpoint) {
            case 'update-vehicle':
                return {
                    status: document.getElementById('vehicle-status').value
                };
            case 'register-user':
                return {
                    username: document.getElementById('username').value,
                    email: document.getElementById('email').value,
                    password: document.getElementById('password').value
                };
            case 'start-trip':
                return {
                    vehicleId: document.getElementById('vehicle-id').value,
                    userId: document.getElementById('user-id').value,
                    startLocation: document.getElementById('start-location').value
                };
            case 'schedule-maintenance':
                return {
                    vehicleId: document.getElementById('vehicle-id').value,
                    maintenanceType: document.getElementById('maintenance-type').value,
                    scheduledDate: document.getElementById('scheduled-date').value
                };
            case 'generate-report':
                return {
                    reportType: document.getElementById('report-type').value,
                    startDate: document.getElementById('start-date').value,
                    endDate: document.getElementById('end-date').value
                };
            case 'create-geofence':
                return {
                    name: document.getElementById('geofence-name').value,
                    coordinates: document.getElementById('coordinates').value.split('\n').map(pair => {
                        const [lat, lon] = pair.split(',');
                        return { lat: parseFloat(lat), lon: parseFloat(lon) };
                    })
                };
            default:
                return {};
        }
    }
}

function showNotification(message, type = 'info') {
    const notificationArea = document.getElementById('notification-area');
    const notification = document.createElement('div');
    notification.className = `notification ${type}`;
    notification.textContent = message;
    notificationArea.appendChild(notification);

    setTimeout(() => {
        notification.classList.add('show');
    }, 10);

    setTimeout(() => {
        notification.classList.remove('show');
        setTimeout(() => {
            notificationArea.removeChild(notification);
        }, 300);
    }, 3000);
}

