function setupAPIInteraction() {
    const apiCategorySelect = document.getElementById('api-category-select');
    const apiEndpointSelect = document.getElementById('api-endpoint-select');
    const optionalParameters = document.getElementById('optional-parameters');
    const accessTokenInput = document.getElementById('access-token');
    const generateTestTokenBtn = document.getElementById('generate-test-token-btn');
    const tryExampleBtn = document.getElementById('try-example-btn');
    const sendRequestBtn = document.getElementById('send-request-btn');
    const requestUrl = document.getElementById('request-url');
    const responseBody = document.getElementById('response-body');
    const loadingSpinner = document.getElementById('loading-spinner');

    apiCategorySelect.addEventListener('change', updateEndpoints);
    apiEndpointSelect.addEventListener('change', updateOptionalParameters);
    generateTestTokenBtn.addEventListener('click', generateTestToken);
    tryExampleBtn.addEventListener('click', loadExample);
    sendRequestBtn.addEventListener('click', sendRequest);

    function updateEndpoints() {
        const category = apiCategorySelect.value;
        apiEndpointSelect.innerHTML = '<option value="">Select an API endpoint</option>';

        const endpoints = getEndpointsForCategory(category);
        endpoints.forEach(endpoint => {
            const option = document.createElement('option');
            option.value = endpoint.value;
            option.textContent = endpoint.label;
            apiEndpointSelect.appendChild(option);
        });
    }

    function getEndpointsForCategory(category) {
        // Implement the logic to return endpoints for each category
        // This is a placeholder and should be replaced with actual endpoints
        return [
            { value: 'get_vehicles', label: 'GET /vehicles' },
            { value: 'create_vehicle', label: 'POST /vehicles' },
            { value: 'update_vehicle', label: 'PUT /vehicles/{id}' },
        ];
    }

    function updateOptionalParameters() {
        // Implement the logic to update optional parameters based on the selected endpoint
        optionalParameters.innerHTML = '';
        const endpoint = apiEndpointSelect.value;
        if (endpoint === 'update_vehicle') {
            optionalParameters.innerHTML = `
                <input type="text" id="vehicle-id" placeholder="Vehicle ID">
                <input type="text" id="vehicle-status" placeholder="New Status">
            `;
        }
    }

    function generateTestToken() {
        fetch('/api/v1/auth/generate-test-token', {
            method: 'POST',
        })
        .then(response => response.json())
        .then(data => {
            accessTokenInput.value = data.test_token;
            showNotification('Test API Access Token generated successfully.', 'success');
        })
        .catch(error => {
            showNotification('Failed to generate Test API Access Token.', 'error');
        });
    }

    function loadExample() {
        const endpoint = apiEndpointSelect.value;
        // Implement logic to load example data for the selected endpoint
        showNotification('Example data loaded.', 'info');
    }

    function sendRequest() {
        const endpoint = apiEndpointSelect.value;
        const accessToken = accessTokenInput.value;

        if (!endpoint || !accessToken) {
            showNotification('Please select an endpoint and enter an access token.', 'error');
            return;
        }

        const url = getApiUrl(endpoint);
        const method = getApiMethod(endpoint);
        const body = getRequestBody(endpoint);

        requestUrl.textContent = `${method}: ${url}`;
        requestUrl.className = 'code-block';

        showNotification('Sending request...', 'info');
        loadingSpinner.style.display = 'block';
        responseBody.style.opacity = '0.5';

        fetch(url, {
            method: method,
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${accessToken}`
            },
            body: method !== 'GET' ? JSON.stringify(body) : undefined
        })
        .then(response => response.json())
        .then(data => {
            responseBody.textContent = JSON.stringify(data, null, 2);
            hljs.highlightElement(responseBody);
            showNotification('Request completed successfully.', 'success');
        })
        .catch(error => {
            responseBody.textContent = `Error: ${error.message}`;
            showNotification('An error occurred while sending the request.', 'error');
        })
        .finally(() => {
            loadingSpinner.style.display = 'none';
            responseBody.style.opacity = '1';
        });
    }

    function getApiUrl(endpoint) {
        // Implement logic to return the correct API URL based on the endpoint
        return `/api/v1/${endpoint.replace('_', '/')}`;
    }

    function getApiMethod(endpoint) {
        // Implement logic to return the correct HTTP method based on the endpoint
        if (endpoint.startsWith('get')) return 'GET';
        if (endpoint.startsWith('create')) return 'POST';
        if (endpoint.startsWith('update')) return 'PUT';
        return 'GET';
    }

    function getRequestBody(endpoint) {
        // Implement logic to return the correct request body based on the endpoint
        if (endpoint === 'update_vehicle') {
            return {
                id: document.getElementById('vehicle-id').value,
                status: document.getElementById('vehicle-status').value
            };
        }
        return {};
    }
}

