    function updateOptionalParameters() {
        optionalParameters.innerHTML = '';
        const endpoint = apiEndpointSelect.value;
        const params = getParametersForEndpoint(endpoint);
        params.forEach(param => {
            const input = document.createElement('input');
            input.type = 'text';
            input.id = param.id;
            input.placeholder = param.placeholder;
            optionalParameters.appendChild(input);
        });
    }

    function getParametersForEndpoint(endpoint) {
        // Define parameters for each endpoint
        const parameters = {
            update_vehicle: [
                { id: 'vehicle-id', placeholder: 'Vehicle ID' },
                { id: 'vehicle-status', placeholder: 'New Status' }
            ],
