        };
        return parameters[endpoint] || [];
    }

    function generateTestToken() {
        fetch('/api/v1/auth/generate-test-token', {
            method: 'POST',
        })
        .then(response => response.json())
        .then(data => {
            accessTokenInput.value = data.test_token;
            showNotification('Test API Access Token generated successfully.', 'success');
        })
        .catch(error => {
            showNotification('Failed to generate Test API Access Token.', 'error');
        });
    }

    function loadExample() {
        const endpoint = apiEndpointSelect.value;
        // Implement logic to load example data for the selected endpoint
        const exampleData = getExampleDataForEndpoint(endpoint);
        Object.keys(exampleData).forEach(key => {
            const input = document.getElementById(key);
            if (input) {
                input.value = exampleData[key];
            }
        });
        showNotification('Example data loaded.', 'info');
    }

    function getExampleDataForEndpoint(endpoint) {
        // Define example data for each endpoint
        const exampleData = {
            update_vehicle: {
                'vehicle-id': '1234',
