:root {
    --primary-color: #3a7bd5;
    --secondary-color: #00d2ff;
    --background-color: #f5f7fa;
    --text-color: #333333;
    --card-background: #ffffff;
    --border-radius: 8px;
    --box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
}

body {
    font-family: 'Roboto', Arial, sans-serif;
    line-height: 1.6;
    margin: 0;
    padding: 0;
    background-color: var(--background-color);
    color: var(--text-color);
}

header {
    background: linear-gradient(to right, var(--primary-color), var(--secondary-color));
    color: #ffffff;
    padding: 1rem;
    display: flex;
    justify-content: space-between;
    align-items: center;
    box-shadow: var(--box-shadow);
}

header h1 {
    margin: 0;
    font-size: 1.5rem;
    font-weight: 700;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 2rem 1rem;
}

.panel-container {
    display: flex;
    gap: 2rem;
    margin-bottom: 2rem;
}

.left-panel, .right-panel {
    flex: 1;
    background-color: var(--card-background);
    border-radius: var(--border-radius);
    padding: 1.5rem;
    box-shadow: var(--box-shadow);
}

.central-section {
    background-color: var(--card-background);
    border-radius: var(--border-radius);
    padding: 1.5rem;
    box-shadow: var(--box-shadow);
    margin-bottom: 2rem;
}

select, input, button, textarea {
    width: 100%;
    padding: 0.75rem;
    margin-bottom: 1rem;
    border: 1px solid #e0e0e0;
    border-radius: var(--border-radius);
    font-size: 1rem;
    transition: all 0.3s ease;
}

select:focus, input:focus, textarea:focus {
    outline: none;
    border-color: var(--primary-color);
    box-shadow: 0 0 0 2px rgba(58, 123, 213, 0.2);
}

button {
    background: linear-gradient(to right, var(--primary-color), var(--secondary-color));
    color: #ffffff;
    border: none;
    cursor: pointer;
    font-weight: bold;
    transition: all 0.3s ease;
}

button:hover {
    opacity: 0.9;
    transform: translateY(-2px);
}

button:active {
    transform: translateY(0);
}

.token-container {
    display: flex;
    gap: 0.5rem;
    margin-bottom: 1rem;
}

.token-container input {
    flex-grow: 1;
    margin-bottom: 0;
}

.token-container button {
    width: auto;
    white-space: nowrap;
}

details {
    margin-bottom: 1rem;
    background-color: #f0f0f0;
    border-radius: var(--border-radius);
    padding: 0.5rem;
}

summary {
    cursor: pointer;
    font-weight: bold;
    padding: 0.5rem;
}

#fleet-map {
    height: 400px;
    border-radius: var(--border-radius);
    overflow: hidden;
    box-shadow: var(--box-shadow);
}

pre {
    background-color: #f4f4f4;
    padding: 1rem;
    border-radius: var(--border-radius);
    overflow-x: auto;
    font-size: 0.9rem;
}

.response-container {
    position: relative;
}

.spinner {
    display: none;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 50px;
    height: 50px;
    border: 5px solid #f3f3f3;
    border-top: 5px solid var(--primary-color);
    border-radius: 50%;
    animation: spin 1s linear infinite;
}

@keyframes spin {
    0% { transform: translate(-50%, -50%) rotate(0deg); }
    100% { transform: translate(-50%, -50%) rotate(360deg); }
}

#notification-area {
    position: fixed;
    top: 1rem;
    right: 1rem;
    z-index: 1000;
}

.notification {
    background-color: #4CAF50;
    color: white;
    padding: 1rem;
    margin-bottom: 0.5rem;
    border-radius: var(--border-radius);
    box-shadow: var(--box-shadow);
    opacity: 0;
    transition: opacity 0.3s ease-in-out;
}

.notification.show {
    opacity: 1;
}

.dashboard-section {
    margin-top: 2rem;
}

.dashboard-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 1rem;
}

.dashboard-card {
    background-color: var(--card-background);
    border-radius: var(--border-radius);
    padding: 1rem;
    box-shadow: var(--box-shadow);
    text-align: center;
}

.dashboard-card h3 {
    margin-top: 0;
    color: var(--primary-color);
}

.dashboard-card p {
    font-size: 1.5rem;
    font-weight: bold;
    margin: 0;
}

.modal {
    display: none;
    position: fixed;
    z-index: 1000;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0,0,0,0.4);
}

.modal-content {
    background-color: var(--card-background);
    margin: 15% auto;
    padding: 2rem;
    border-radius: var(--border-radius);
    box-shadow: var(--box-shadow);
    width: 80%;
    max-width: 600px;
}

.close {
    color: #aaa;
    float: right;
    font-size: 28px;
    font-weight: bold;
    cursor: pointer;
}

.close:hover,
.close:focus {
    color: #000;
    text-decoration: none;
    cursor: pointer;
}

@media (max-width: 768px) {
    .panel-container {
        flex-direction: column;
    }

    .left-panel, .right-panel {
        width: 100%;
    }
}

/* Dark mode styles */
body.dark-mode {
    --background-color: #1a1a1a;
    --text-color: #f4f4f4;
    --card-background: #2c2c2c;
}

body.dark-mode header {
    background: linear-gradient(to right, #1a1a1a, #2c2c2c);
}

body.dark-mode pre {
    background-color: #3a3a3a;
    color: #f4f4f4;
}

body.dark-mode #advanced-options,
body.dark-mode #batch-options {
    background-color: #3a3a3a;
}

body.dark-mode .notification {
    background-color: #2c3e50;
}

/* Improved accessibility */
.visually-hidden {
    position: absolute;
    width: 1px;
    height: 1px;
    margin: -1px;
    padding: 0;
    overflow: hidden;
    clip: rect(0, 0, 0, 0);
    white-space: nowrap;
    border: 0;
}

/* Improved focus styles */
*:focus {
    outline: 2px solid var(--primary-color);
    outline-offset: 2px;
}

/* Improved button styles */
.icon-button {
    background: none;
    border: none;
    font-size: 1.5rem;
    cursor: pointer;
    padding: 0.5rem;
    border-radius: 50%;
    transition: background-color 0.3s ease;
}

.icon-button:hover {
    background-color: rgba(255, 255, 255, 0.1);
}

/* Improved form styles */
label {
    display: block;
    margin-bottom: 0.5rem;
    font-weight: bold;
}

/* Improved code block styles */
.code-block {
    background-color: #f4f4f4;
    border-radius: var(--border-radius);
    padding: 1rem;
    margin-bottom: 1rem;
    font-family: monospace;
    white-space: pre-wrap;
    word-break: break-all;
}

body.dark-mode .code-block {
    background-color: #2c2c2c;
    color: #f4f4f4;
}

