document.addEventListener('DOMContentLoaded', () => {
    initializeMap();
    setupDarkModeToggle();
    setupAPIInteraction();
    setupHelpModal();
    updateDashboard();
});

function initializeMap() {
    mapboxgl.accessToken = MAPBOX_ACCESS_TOKEN;
    const map = new mapboxgl.Map({
        container: 'fleet-map',
        style: 'mapbox://styles/mapbox/streets-v11',
        center: [-122.4194, 37.7749],
        zoom: 12
    });

    // Add markers for sample vehicles
    const vehicles = [
        { id: 1, coordinates: [-122.4194, 37.7749], status: 'active' },
        { id: 2, coordinates: [-122.4099, 37.7850], status: 'maintenance' },
        { id: 3, coordinates: [-122.4289, 37.7648], status: 'inactive' },
    ];

    vehicles.forEach(vehicle => {
        const el = document.createElement('div');
        el.className = 'vehicle-marker';
        el.style.backgroundColor = getVehicleColor(vehicle.status);
        el.style.width = '20px';
        el.style.height = '20px';
        el.style.borderRadius = '50%';
        el.style.border = '2px solid white';

        new mapboxgl.Marker(el)
            .setLngLat(vehicle.coordinates)
            .setPopup(new mapboxgl.Popup().setHTML(`Vehicle ${vehicle.id}<br>Status: ${vehicle.status}`))
            .addTo(map);
    });

    // Add zoom and rotation controls
    map.addControl(new mapboxgl.NavigationControl());

    // Add fullscreen control
    map.addControl(new mapboxgl.FullscreenControl());
}

function getVehicleColor(status) {
    switch (status) {
        case 'active': return '#22c55e';
        case 'maintenance': return '#eab308';
        case 'inactive': return '#ef4444';
        default: return '#3b82f6';
    }
}

function setupDarkModeToggle() {
    const darkModeToggle = document.getElementById('dark-mode-toggle');
    const body = document.body;

    darkModeToggle.addEventListener('click', () => {
        body.classList.toggle('dark-mode');
        localStorage.setItem('darkMode', body.classList.contains('dark-mode'));
        darkModeToggle.textContent = body.classList.contains('dark-mode') ? '🌞' : '🌓';
        darkModeToggle.setAttribute('aria-label', body.classList.contains('dark-mode') ? 'Switch to Light Mode' : 'Switch to Dark Mode');
    });

    if (localStorage.getItem('darkMode') === 'true') {
        body.classList.add('dark-mode');
        darkModeToggle.textContent = '🌞';
        darkModeToggle.setAttribute('aria-label', 'Switch to Light Mode');
    }
}

function setupAPIInteraction() {
    const apiCategorySelect = document.getElementById('api-category-select');
    const apiEndpointSelect = document.getElementById('api-endpoint-select');
    const optionalParameters = document.getElementById('optional-parameters');
    const accessTokenInput = document.getElementById('access-token');
    const generateTestTokenBtn = document.getElementById('generate-test-token-btn');
    const tryExampleBtn = document.getElementById('try-example-btn');
    const sendRequestBtn = document.getElementById('send-request-btn');
    const requestUrl = document.getElementById('request-url');
    const responseBody = document.getElementById('response-body');
    const loadingSpinner = document.getElementById('loading-spinner');

    apiCategorySelect.addEventListener('change', updateEndpoints);
    apiEndpointSelect.addEventListener('change', updateOptionalParameters);
    generateTestTokenBtn.addEventListener('click', generateTestToken);
    tryExampleBtn.addEventListener('click', loadExample);
    sendRequestBtn.addEventListener('click', sendRequest);

    function updateEndpoints() {
        const category = apiCategorySelect.value;
        apiEndpointSelect.innerHTML = '<option value="">Select an API endpoint</option>';

        const endpoints = getEndpointsForCategory(category);
        endpoints.forEach(endpoint => {
            const option = document.createElement('option');
            option.value = endpoint.value;
            option.textContent = endpoint.label;
            apiEndpointSelect.appendChild(option);
        });
    }

    function getEndpointsForCategory(category) {
        const endpoints = {
            fleet: [
                { value: 'get_vehicles', label: 'GET /vehicles' },
                { value: 'create_vehicle', label: 'POST /vehicles' },
                { value: 'update_vehicle', label: 'PUT /vehicles/{id}' },
                { value: 'get_fleet_status', label: 'GET /fleet/status' }
            ],
            user: [
                { value: 'get_user', label: 'GET /user' },
                { value: 'create_user', label: 'POST /user' },
                { value: 'update_user', label: 'PUT /user/{id}' },
                { value: 'get_user_activity', label: 'GET /user/activity' }
            ],
            trip: [
                { value: 'start_trip', label: 'POST /trip' },
                { value: 'end_trip', label: 'PUT /trip/{id}' },
                { value: 'get_trip', label: 'GET /trip/{id}' },
                { value: 'list_trips', label: 'GET /trips' }
            ],
            maintenance: [
                { value: 'create_maintenance_task', label: 'POST /maintenance' },
                { value: 'update_maintenance_task', label: 'PUT /maintenance/{id}' },
                { value: 'get_maintenance_history', label: 'GET /maintenance/history' },
                { value: 'get_predictive_maintenance', label: 'GET /maintenance/predictive' }
            ],
            analytics: [
                { value: 'get_usage_stats', label: 'GET /analytics/usage' },
                { value: 'generate_report', label: 'POST /analytics/report' },
                { value: 'get_report', label: 'GET /analytics/report/{id}' }
            ],
            payment: [
                { value: 'create_invoice', label: 'POST /payment/invoice' },
                { value: 'get_invoice', label: 'GET /payment/invoice/{id}' },
                { value: 'process_payment', label: 'POST /payment/process' }
            ],
            location: [
                { value: 'create_geofence', label: 'POST /location/geofence' },
                { value: 'update_geofence', label: 'PUT /location/geofence/{id}' },
                { value: 'get_geofences', label: 'GET /location/geofences' }
            ]
        };
        return endpoints[category] || [];
    }

    function updateOptionalParameters() {
        optionalParameters.innerHTML = '';
        const endpoint = apiEndpointSelect.value;
        const params = getParametersForEndpoint(endpoint);
        params.forEach(param => {
            const label = document.createElement('label');
            label.textContent = param.label;
            label.setAttribute('for', param.id);

            const input = document.createElement('input');
            input.type = 'text';
            input.id = param.id;
            input.placeholder = param.placeholder;

            optionalParameters.appendChild(label);
            optionalParameters.appendChild(input);
        });
    }

    function getParametersForEndpoint(endpoint) {
        const parameters = {
            update_vehicle: [
                { id: 'vehicle-id', label: 'Vehicle ID:', placeholder: 'Enter Vehicle ID' },
                { id: 'vehicle-status', label: 'New Status:', placeholder: 'Enter new status' }
            ],
            create_maintenance_task: [
                { id: 'vehicle-id', label: 'Vehicle ID:', placeholder: 'Enter Vehicle ID' },
                { id: 'task-description', label: 'Task Description:', placeholder: 'Enter task description' }
            ],
            // Add more parameters for other endpoints as needed
        };
        return parameters[endpoint] || [];
    }

    function generateTestToken() {
        fetch('/api/v1/auth/generate-test-token', {
            method: 'POST',
        })
        .then(response => response.json())
        .then(data => {
            accessTokenInput.value = data.test_token;
            showNotification('Test API Access Token generated successfully.', 'success');
        })
        .catch(error => {
            showNotification('Failed to generate Test API Access Token.', 'error');
        });
    }

    function loadExample() {
        const endpoint = apiEndpointSelect.value;
        const exampleData = getExampleDataForEndpoint(endpoint);
        Object.keys(exampleData).forEach(key => {
            const input = document.getElementById(key);
            if (input) {
                input.value = exampleData[key];
            }
        });
        showNotification('Example data loaded.', 'info');
    }

    function getExampleDataForEndpoint(endpoint) {
        const exampleData = {
            update_vehicle: {
                'vehicle-id': '1234',
                'vehicle-status': 'maintenance'
            },
            create_maintenance_task: {
                'vehicle-id': '5678',
                'task-description': 'Routine oil change'
            },
            // Add more example data for other endpoints as needed
        };
        return exampleData[endpoint] || {};
    }

    function sendRequest() {
        const endpoint = apiEndpointSelect.value;
        const accessToken = accessTokenInput.value;

        if (!endpoint || !accessToken) {
            showNotification('Please select an endpoint and enter an access token.', 'error');
            return;
        }

        const url = getApiUrl(endpoint);
        const method = getApiMethod(endpoint);
        const body = getRequestBody(endpoint);

        requestUrl.textContent = `${method}: ${url}`;
        requestUrl.className = 'code-block';

        showNotification('Sending request...', 'info');
        loadingSpinner.style.display = 'block';
        responseBody.style.opacity = '0.5';

        fetch(url, {
            method: method,
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${accessToken}`
            },
            body: method !== 'GET' ? JSON.stringify(body) : undefined
        })
        .then(response => response.json())
        .then(data => {
            responseBody.textContent = JSON.stringify(data, null, 2);
            hljs.highlightElement(responseBody);
            showNotification('Request completed successfully.', 'success');
        })
        .catch(error => {
            responseBody.textContent = `Error: ${error.message}`;
            showNotification('An error occurred while sending the request.', 'error');
        })
        .finally(() => {
            loadingSpinner.style.display = 'none';
            responseBody.style.opacity = '1';
        });
    }

    function getApiUrl(endpoint) {
        return `/api/v1/${endpoint.replace('_', '/')}`;
    }

    function getApiMethod(endpoint) {
        if (endpoint.startsWith('get')) return 'GET';
        if (endpoint.startsWith('create')) return 'POST';
        if (endpoint.startsWith('update')) return 'PUT';
        if (endpoint.startsWith('delete')) return 'DELETE';
        return 'GET';
    }

    function getRequestBody(endpoint) {
        const body = {};
        const params = getParametersForEndpoint(endpoint);
        params.forEach(param => {
            const input = document.getElementById(param.id);
            if (input && input.value) {
                body[param.id] = input.value;
            }
        });
        return body;
    }
}

function setupHelpModal() {
    const modal = document.getElementById('help-modal');
    const btn = document.getElementById('help-button');
    const closeBtn = modal.querySelector('.close');

    btn.onclick = function() {
        modal.style.display = 'block';
        modal.setAttribute('aria-hidden', 'false');
    }

    closeBtn.onclick = function() {
        modal.style.display = 'none';
        modal.setAttribute('aria-hidden', 'true');
    }

    window.onclick = function(event) {
        if (event.target == modal) {
            modal.style.display = 'none';
            modal.setAttribute('aria-hidden', 'true');
        }
    }
}

function updateDashboard() {
    // This function should fetch real data from your API
    // For now, we'll use placeholder data
    document.getElementById('total-vehicles').textContent = '150';
    document.getElementById('active-trips').textContent = '42';
    document.getElementById('maintenance-tasks').textContent = '7';
    document.getElementById('revenue-today').textContent = '$3,250';
}

function showNotification(message, type = 'info') {
    const notificationArea = document.getElementById('notification-area');
    const notification = document.createElement('div');
    notification.className = `notification ${type}`;
    notification.textContent = message;
    notification.setAttribute('role', 'alert');
    notificationArea.appendChild(notification);

    setTimeout(() => {
        notification.classList.add('show');
    }, 10);

    setTimeout(() => {
        notification.classList.remove('show');
        setTimeout(() => {
            notificationArea.removeChild(notification);
        }, 300);
    }, 3000);
}

