}

.endpoint-item:hover {
    transform: translateY(-5px);
}

.endpoint-item h3 {
    margin-top: 0;
    color: var(--primary-color);
}

.endpoint-item p {
    font-size: 0.9rem;
    margin-bottom: 0.5rem;
}

.endpoint-item button {
    width: 100%;
}

/* Improved layout for smaller screens */
@media (max-width: 600px) {
    .endpoint-list {
        grid-template-columns: 1fr;
    }
}

/* Tooltip styles */
.tooltip {
    position: relative;
    display: inline-block;
}

.tooltip .tooltiptext {
    visibility: hidden;
    width: 200px;
    background-color: #555;
    color: #fff;
    text-align: center;
    border-radius: 6px;
    padding: 5px;
    position: absolute;
    z-index: 1;
    bottom: 125%;
    left: 50%;
    margin-left: -100px;
    opacity: 0;
    transition: opacity 0.3s;
}

.tooltip:hover .tooltiptext {
    visibility: visible;
    opacity: 1;
}

